    """
    Change unicode output into bytestrings in Python 2
    """
    @functools.wraps(namefunc)
    def wrapper(*args, **kwargs):
        result = namefunc(*args, **kwargs)
        if six.PY2 and isinstance(result, six.text_type):
            return result.encode('utf-8')
        return result
    return wrapper

