def parse_subparser_arguments(unparsed_arguments, subparsers):
    parsed_namespaces = {}
    remaining_args = list(unparsed_arguments)
    for name, parser in subparsers.items():
        if not remaining_args:
            break
        if remaining_args[0] == name:
            args = remaining_args[1:]
            parsed = parser.parse_args(args)
            parsed_namespaces[name] = parsed
            remaining_args = []
    return parsed_namespaces, remaining_args

def _dictsum(dicts):
    """
    Sum values with the same key in the dictionaries. And return a new dictionary.
    Example:
        given two dictionaries: dict1: {'a': 1, 'b': 2}, dict2: {'a': 5, 'b': 0}
        return a dictionary: dic:{'a': 6, 'b': 2}
    Returns: dict
    """
    result = {}
    for d in dicts:
        for k, v in d.items():
            if k in result:
                result[k] += v
            else:
                result[k] = v
    return result

from typing import List, Optional

def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short uuid.
    The output has the most significant digit first.
    @param number: Int value
    @param alphabet : List with letters
    @param padding : Optional with int value
    @return  string value corresponded to int
    """
    if number == 0:
        return alphabet[0] * (padding if padding is not None else 1)
    base = len(alphabet)
    digits = []
    while number > 0:
        remainder = number % base
        digits.append(alphabet[remainder])
        number = number // base
    result = ''.join(reversed(digits))
    if padding is not None:
        result = alphabet[0] * (padding - len(result)) + result
    return result

def subclasses(cls):
    """
    Return all subclasses of a class, recursively
    """
    result = set()
    for sub in cls.__subclasses__():
        result.add(sub)
        result.update(subclasses(sub))
    return list(result)

