def determineMetaclass(bases, explicit_mc=None):
    """
    Determine metaclass from 1+ bases and optional explicit __metaclass__
    """
    if explicit_mc is not None:
        return explicit_mc
    metas = [type(b) for b in bases]
    if len(set(metas)) == 1:
        return metas[0]
    for meta in metas:
        if all(issubclass(m, meta) for m in metas):
            return meta
    raise TypeError("metaclass conflict")

def pop(self, key, default=__marker):
    """
    D.pop(k[,d]) -> v, remove specified key and return the corresponding value.
    If key is not found, d is returned if given, otherwise KeyError is raised.
    """
    if key in self:
        value = self[key]
        del self[key]
        return value
    elif default is not __marker:
        return default
    else:
        raise KeyError(key)

def popitem(self):
    """
    Remove and return the `(key, value)` pair least frequently used.
    """
    if not self.__counter:
        raise KeyError('cache is empty')
    min_freq = min(self.__counter.values())
    for key, freq in self.__counter.items():
        if freq == min_freq:
            break
    value = self.cache[key]
    del self.cache[key]
    del self.__counter[key]
    return (key, value)

def popitem(self):
    """
    Remove and return the (key, value) pair most recently used.
    """
    if not self.__order:
        raise KeyError('cache is empty')
    key = self.__order.pop()
    value = self._cache.pop(key)
    return (key, value)

import re
from typing import Pattern

def _create_in_regex(self) -> Pattern:
    """
    Create the in-style parameter regular expression.

    Returns the in-style parameter regular expression (:class:`re.Pattern`).
    """
    if self._in_style == 'named':
        pattern = r':[a-zA-Z_][a-zA-Z0-9_]*'
    elif self._in_style == 'qmark':
        pattern = r'\?'
    elif self._in_style == 'format':
        pattern = r'%s'
    elif self._in_style == 'pyformat':
        pattern = r'%\([a-zA-Z_][a-zA-Z0-9_]*\)s'
    else:
        raise ValueError(f"Unsupported in_style: {self._in_style}")
    return re.compile(pattern)

def _create_converter(self) -> _converting._Converter:
    """
    Create the parameter style converter.

    Returns the parameter style converter (:class:`._converting._Converter`).
    """
    return _converting._Converter(self._in_style, self._out_style, self._escape_char, self._expand_tuples)

import json
from lxml import etree

def identify_request(request: RequestType):
    """
    Check whether the request body loaded using JSON contains events. If yes, True is returned, otherwise, check whether the tag of the XML loaded from the request body is Magic_ENV_TAG, if yes, return True. If neither of the preceding conditions is met, return False.
    """
    try:
        json_data = json.loads(request.body)
        if 'events' in json_data:
            return True
    except (ValueError, TypeError):
        pass

    try:
        xml_root = etree.fromstring(request.body.encode('utf-8'))
        if xml_root.tag == MAGIC_ENV_TAG:
            return True
    except etree.XMLSyntaxError:
        pass

    return False

