def validate_requires_args(self, args):
    """
    Check if all the required arguments have been provided.
    """
    for arg, spec in self.spec_dict.items():
        if spec.get('required', False) and arg not in args:
            raise ValueError(f"Missing required argument: {arg}")

def validate_length_args(self, args):
    """
    Check if value of the given args is not longer than length specified. \n:param args: The received arguments.
    """
    for arg_name, arg_value in args.items():
        max_length = self.spec_helper.get(arg_name, {}).get('max_length')
        if max_length is not None and len(arg_value) > max_length:
            raise ValueError(f"The value for argument '{arg_name}' exceeds the maximum allowed length of {max_length}.")

def validate_choices_args(self, args):
    """
    Check if value of the given args is one of the available choices. \n:param args: The received arguments.
    """
    for arg_name, arg_value in args.items():
        if arg_name in self.spec_dict and 'choices' in self.spec_dict[arg_name]:
            choices = self.spec_dict[arg_name]['choices']
            if arg_value not in choices:
                raise ValueError(f"Invalid choice for {arg_name}: {arg_value}. Available choices: {choices}")

def validate_min_max_args(self, args):
    """
    Check if value of the given args is between minimum and maximum values
    """
    for arg in args:
        if hasattr(arg, 'min') and hasattr(arg, 'max'):
            if not (arg.min <= arg.value <= arg.max):
                raise ValueError(f"Value {arg.value} for argument {arg.name} is out of the allowed range [min={arg.min}, max={arg.max}]")

def get_nested_custom_and_control_args(self, args):
    """
    Split input arguments to control nested and custom.

    Controls arguments: control the IR behavior. These arguments
        will not be put into the spec yml file
    Nested arguments: are used by the Ansible playbooks and will be put
        into the spec yml file.
    Custom arguments: Custom ansible variables to be used instead of the
        normal nested usage.

    :param args: the collected list of args.
    :return: (dict, dict): flat dicts (control_args, nested_args)
    """
    control_args = {}
    nested_args = {}
    for key, value in args.items():
        if key.startswith('control_'):
            control_args[key] = value
        elif key.startswith('custom_'):
            nested_args[key] = value
        else:
            nested_args[key] = value
    return control_args, nested_args

